import '../css/reason.css';

function Reason() {
  return (
    <div className="Reason">
      <div> <p>เหตุผลที่ควรกินเจ</p> </div>
      <div className="reason1"> 
      <h6 className="font" >1. กินเพื่อสุขภาพอาหารเจเป็นอาหารประเภทชีวจิต<br></br>
สามารถขับพิษของเสียต่าง ๆออกจากร่างกายได้
ปรับระบบไหลเวียนโลหิต
</h6></div>
      <div className="reason2"> 
      <h6 className="font" >2. กินเจ เพื่อทำบถญ เพื่อชำระล้างใจให้ใสสะอาดไม่บียดดบียนสัตว์โลก</h6></div>
      <div className="reason3"> 
      <h6 className="font" >3. กินเพื่อเว้นกรรม หันมารับประทานอาหารเจ ซึ่งทำให้ร่างกายเติบโตได้เหมือนกัน โดยไม่ต้องฆ่าสัตว์</h6></div>
    </div>
  );
}

export default Reason;
