import React from 'react';
import '../css/food.css';
import mushroom from '../image/mushroom.png';
import tofu from '../image/tofu.png';
import cabage from '../image/cabage.png';
import noodle from '../image/noodle.png';


class Food extends React.Component{
  state={
    content : "เมนูอาหารจากเห็ด"
  }
  Changetofu=()=>{
    this.setState({content : "อาหารจากเต้าหู้" })
  }

Changecabage=()=>{
  this.setState({content : "อาหารจากกะหล่ำ" })
}

Changenoodle=()=>{
  this.setState({content : "อาหารจากเส้น" })
}
render(){
    return (
      <div className="Food">
        <div className="foodtopic">FOOD</div>
        <img src={mushroom} width='250pt'onClick={this.state.content}/>
        <img src={tofu} width='250pt' onClick={this.Changetofu}/>
        <img src={cabage} width='250pt' onClick={this.Changecabage}/>
        <img src={noodle} width='250pt' onClick={this.Changenoodle}/>
        <p>{this.state.content}</p>
      </div>
    )
}
}
export default Food;
